    private void btnOkActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnOkActionPerformed
        String palabra = txtPalabra.getText();
        String palabrasinvoc = palabra.replaceAll("[AEIOUaeiou]", "");
        
        lblSalida1.setText(palabrasinvoc);
        
        String palabrasincons = palabra.replaceAll("[BCDFGHJKLMNPQRSTVXZbcdfghjklmnpqrstvxz]", "");
        lblSalida2.setText(palabrasincons);
        
        String vocalmayus = palabra;
        vocalmayus = vocalmayus.replace('a', 'A');
        vocalmayus = vocalmayus.replace('e', 'E');
        vocalmayus = vocalmayus.replace('i', 'I');
        vocalmayus  =vocalmayus.replace('o', 'O');
        vocalmayus = vocalmayus.replace('u', 'U');
        
        lblSalida3.setText(vocalmayus);
    }//GEN-LAST:event_btnOkActionPerformed

