    private void btnGenerarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGenerarActionPerformed
        int contador = 0;
        int incremento = 0;
        String resultado = "";
        int suma = 0;
        
        while (contador < 50) {
               contador += 1;
               incremento += 2;  
               resultado += String.valueOf(incremento) + ',';  
               suma += incremento;
        }
        
        lblSalida.setText(resultado);  
        lblSalida2.setText(String.valueOf(suma)); 
    }//GEN-LAST:event_btnGenerarActionPerformed

